  try{
    const user = new User({
      name:'abcde',
      email:'abcde@example.com',
      password:'0101',
      isSeller:true,
    });
    const newUser = await user.save();
    res.send(newUser)
  }catch(error){
    res.send({msg:error.message});
  }
})

router.get('/delivery-person' , async(req,res) =>{
  try{
    const user = new User({
      name:'def',
      email:'def@example.com',
      password:'0000',
      isDeliveryPerson:true

    });
    const newUser = await user.save();
    res.send(newUser);
  }catch(error){
    res.send({msg:error.message});
  }
})

export default router;

